import React from 'react';
import {SentimentslistType} from "../../type/types";


export let ListArticle: React.FC<SentimentslistType> = ({articlesentence, articlesentiment, sentimentscore}) => {
    return (
        <React.Fragment>
            {articlesentiment === 'negative' && <p style={{margin:'0',textDecoration: 'underline',textDecorationColor: 'red',textUnderlineOffset: '1.5px'}}>{articlesentence}.&nbsp;
            <span style={{color:'red'}}>({articlesentiment}</span> <span style={{color:'red'}}>{sentimentscore?.toFixed(1)})</span></p>}
              {articlesentiment === 'neutral' && <p style={{margin:'0',textDecoration: 'underline',textDecorationColor: '#649DD1',textUnderlineOffset: '1.5px'}}>{articlesentence}.&nbsp;
              <span style={{color:'#649DD1'}}>({articlesentiment}</span> <span style={{color:'#649DD1'}}>{sentimentscore?.toFixed(1)})</span></p>}
              {articlesentiment === 'positive' && <p style={{margin:'0',textDecoration: 'underline',textDecorationColor: 'green',textUnderlineOffset: '1.5px'}}>{articlesentence}.&nbsp;
              <span style={{color:'green', alignItems:'end'}}>({articlesentiment}</span> <span style={{color:'green', alignItems:'end'}}>{sentimentscore?.toFixed(1)})</span></p>}
        </React.Fragment>
    )
}
